$(".top-wrapper__text").each(function(){let e=$(this).find("p");if(e.text().length>200){let t=e.text().substring(0,200),i=$(this).find(".see-more-btn");i.remove(),e.addClass("fullArticle").hide(),$(this).append('<p class="preview">'+t+"</p>"),$(this).append(i)}else $(this).find(".see-more-btn").hide()}),$(".see-more-btn").on("click",function(e){e.preventDefault(),$(this).parent().find($(".preview")).hide(),$(this).parent().find($(".fullArticle")).show(),$(this).hide()});